impl AveragedCollection { pub fn add(&mut self, value: i32) { self.list.push(value); self.update_average(); } pub fn remove(&mut self) -> Option<i32> { let result = self.list.pop(); match result { Some(value) => { self.update_average(); Some(value) }, None => None, } } pub fn average(&self) -> f64 { self.average } fn update_average(&mut self) { let total: i32 = self.list.iter().sum(); self.average = total as f64 / self.list.len() as f64; }
} ç¤ºä¾‹ 17-2: åœ¨AveragedCollection ç»“æ„ä½“ä¸Šå®ç°äº†addã€remove å’Œ average å…¬æœ‰æ–¹æ³• å…¬æœ‰æ–¹æ³• addã€remove å’Œ average æ˜¯ä¿®æ”¹ AveragedCollection å®ä¾‹çš„å”¯ä¸€æ–¹å¼ã€‚å½“ä½¿ç”¨ add æ–¹æ³•æŠŠä¸€ä¸ªå…ƒç´ åŠ å…¥åˆ° list æˆ–è€…ä½¿ç”¨ remove æ–¹æ³•æ¥åˆ é™¤æ—¶ï¼Œè¿™äº›æ–¹æ³•çš„å®ç°åŒæ—¶ä¼šè°ƒç”¨ç§æœ‰çš„ update_average æ–¹æ³•æ¥æ›´æ–° average å­—æ®µã€‚ list å’Œ average æ˜¯ç§æœ‰çš„ï¼Œæ‰€ä»¥æ²¡æœ‰å…¶ä»–æ–¹å¼æ¥ä½¿å¾—å¤–éƒ¨çš„ä»£ç ç›´æ¥å‘ list å¢åŠ æˆ–è€…åˆ é™¤å…ƒç´ ï¼Œå¦åˆ™ list æ”¹å˜æ—¶å¯èƒ½ä¼šå¯¼è‡´ average å­—æ®µä¸åŒæ­¥ã€‚average æ–¹æ³•è¿”å› average å­—æ®µçš„å€¼ï¼Œè¿™ä½¿å¾—å¤–éƒ¨çš„ä»£ç åªèƒ½è¯»å– average è€Œä¸èƒ½ä¿®æ”¹å®ƒã€‚ å› ä¸ºæˆ‘ä»¬å·²ç»å°è£…å¥½äº† AveragedCollection çš„å®ç°ç»†èŠ‚ï¼Œå°†æ¥å¯ä»¥è½»æ¾æ”¹å˜ç±»ä¼¼æ•°æ®ç»“æ„è¿™äº›æ–¹é¢çš„å†…å®¹ã€‚ä¾‹å¦‚ï¼Œå¯ä»¥ä½¿ç”¨ HashSet<i32> ä»£æ›¿ Vec<i32> ä½œä¸º list å­—æ®µçš„ç±»å‹ã€‚åªè¦ addã€remove å’Œ average å…¬æœ‰å‡½æ•°çš„ç­¾åä¿æŒä¸å˜ï¼Œä½¿ç”¨ AveragedCollection çš„ä»£ç å°±æ— éœ€æ”¹å˜ã€‚ç›¸åå¦‚æœä½¿å¾— list ä¸ºå…¬æœ‰ï¼Œå°±æœªå¿…éƒ½ä¼šå¦‚æ­¤äº†ï¼š HashSet<i32> å’Œ Vec<i32> ä½¿ç”¨ä¸åŒçš„æ–¹æ³•å¢åŠ æˆ–ç§»é™¤é¡¹ï¼Œæ‰€ä»¥å¦‚æœè¦æƒ³ç›´æ¥ä¿®æ”¹ list çš„è¯ï¼Œå¤–éƒ¨çš„ä»£ç å¯èƒ½ä¸å¾—ä¸åšå‡ºä¿®æ”¹ã€‚ å¦‚æœå°è£…æ˜¯ä¸€ä¸ªè¯­è¨€è¢«è®¤ä¸ºæ˜¯é¢å‘å¯¹è±¡è¯­è¨€æ‰€å¿…è¦çš„æ–¹é¢çš„è¯ï¼Œé‚£ä¹ˆ Rust æ»¡è¶³è¿™ä¸ªè¦æ±‚ã€‚åœ¨ä»£ç ä¸­ä¸åŒçš„éƒ¨åˆ†ä½¿ç”¨ pub ä¸å¦å¯ä»¥å°è£…å…¶å®ç°ç»†èŠ‚ã€‚

